import throttle from 'lodash.throttle'
import Icon from './Icon.vue'

export default {
  name: 'TheHeaderNavbar',

  components: {
    Icon,
  },

  data () {
    return {
      fixed: false,
      showNavLinks: false,
    }
  },

  computed: {
    navbarClass () {
      return {
        'fixed': this.fixed,
      }
    },
  },

  mounted () {
    window.addEventListener('scroll', throttle(() => {
      this.fixed = window.scrollY !== 0
    }), 100)
  },

  methods: {
    isExternal (link) {
      return /^(https?:|mailto:|tel:)/.test(link)
    },
  },
}
