export default {
  name: 'TransitionFadeSlide',

  functional: true,

  props: {
    appear: {
      type: Boolean,
      default: true,
    },

    direction: {
      type: String,
      default: 'y',
    },

    group: {
      type: Boolean,
      default: false,
    },

    mode: {
      type: String,
      default: 'out-in',
    },

    tag: {
      type: String,
      default: '',
    },
  },

  render (h, { props, children }) {
    return h(
      props.group ? 'TransitionGroup' : 'Transition',
      {
        props: {
          name: `fade-slide-${props.direction}`,
          ...props,
        },
      },
      children,
    )
  },

  computed: {
    name () {
      return `fade-slide-${this.direction}`
    },

    component () {
      return this.group ? 'transition-group' : 'transition'
    },
  },
}
