import compareDesc from 'date-fns/compare_desc';
import TransitionFadeSlide from './TransitionFadeSlide.vue';
import PostsListItem from './PostsListItem.vue';
export default {
  name: 'PostsList',
  components: {
    TransitionFadeSlide: TransitionFadeSlide,
    PostsListItem: PostsListItem
  },
  props: {
    posts: {
      type: Array,
      required: false,
      default: null
    }
  },
  computed: {
    listPosts: function listPosts() {
      var listPosts = this.posts || this.$posts;

      var pageSort = function pageSort(p1, p2) {
        if (p1.top === p2.top) {
          return compareDesc(p1.createdAt, p2.createdAt);
        }

        if (p1.top && p2.top) {
          return p1.top - p2.top;
        }

        return p2.top ? 1 : -1;
      };

      return listPosts.sort(pageSort);
    }
  }
};
